package controllers.yh;import interceptor.SetAttrLoginUserInterceptor;import java.io.BufferedReader;import java.io.FileReader;import java.io.IOException;import org.apache.shiro.authz.annotation.RequiresAuthentication;import com.jfinal.aop.Before;import com.jfinal.core.Controller;import com.jfinal.log.Logger;//@RequiresAuthentication@Before(SetAttrLoginUserInterceptor.class)public class LogController extends Controller {    private Logger logger = Logger.getLogger(LogController.class);    public void index() {        try {            renderText(readFileAsString("logs/eeda"));        } catch (IOException e) {            e.printStackTrace();            renderText("debug log can not find.");        }    }    private String readFileAsString(String filePath) throws IOException {        StringBuffer fileData = new StringBuffer();        BufferedReader reader = new BufferedReader(new FileReader(filePath));        char[] buf = new char[1024];        int numRead = 0;        while ((numRead = reader.read(buf)) != -1) {            String readData = String.valueOf(buf, 0, numRead);            fileData.append(readData);        }        reader.close();        return fileData.toString();    }}