package models.yh.profile;import java.util.LinkedHashMap;import java.util.List;import java.util.Map;import com.jfinal.plugin.activerecord.Model;public class Party extends Model<Party> {	public static final Party dao = new Party();	public Map<String, String> getAttr() {		List<PartyAttribute> attrList = PartyAttribute.dao.find("select * from party_attribute where party_id=?", get("id"));		Map<String, String> map = new LinkedHashMap<String, String>();		for (PartyAttribute partyAttribute : attrList) {			map.put(partyAttribute.getStr("attr_name"), partyAttribute.getStr("attr_value"));		}		return map;	}}